import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Tab, Row, Col, ListGroup, Button, Card, Container } from 'react-bootstrap'
import { Employee } from './Employee'

export default function GetAllEmployee() {

    const [EmployeeList, setEmployeeList] = useState<Employee[]>([])

    useEffect(() => {
        axios.get<Employee[]>("https://localhost:44321")
        .then(res=>{
            setEmployeeList(res.data)
        })
    }, [])

    return (
        <Container>
             <Row>
                 {EmployeeList.map((employee)=>{
                     return <Col md={4}>                    
                                <Card style={{ width: '18rem' }}>
                                    <Card.Img variant="top" src="holder.js/100px180" />
                                    <Card.Body>
                                        <Card.Title>{employee.fullName}</Card.Title>
                                        <Card.Title>{employee.deptName}</Card.Title>
                                        <Card.Title>{employee.designationName}</Card.Title>
                                        <Card.Text>{employee.description}</Card.Text>
                                        <Button variant="primary">Go somewhere</Button>
                                    </Card.Body>
                                </Card>
                            </Col>
                 })}
                
            </Row>
        </Container>
       
    )
}
